(()=>{"use strict";const t="http://127.0.0.1:8000";window.onload=function(){!async function(e){try{const e=await fetch(`${t}/service/qna/123/`,{headers:{"Content-Type":"application/json"},method:"GET"});if(!e.ok)throw new Error(`Server returned an error ${e.status}: ${e.statusText}`);const n=await e.json();document.getElementById("faqCategory").textContent=n.question_type,document.getElementById("faqTitle").textContent=n.title,document.getElementById("faqAuthor").textContent=n.username,document.getElementById("faqDate").textContent=n.created_at,document.getElementById("faqContent").textContent=n.content;const o=document.getElementById("lockContainer");if(n.is_private){const t=!0;o.style.display=t?"none":"block"}else o.style.display="none"}catch(t){console.error("Error fetching FAQ detail:",t)}}(),async function(e){try{const e=await fetch(`${t}/service/qna/123/response/`,{headers:{"Content-Type":"application/json"},method:"GET"});if(!e.ok)throw new Error(`Server returned an error ${e.status}: ${e.statusText}`);const n=await e.json();null==n.content?(document.getElementById("editAnswerButton").style.display="none",document.getElementById("deleteAnswerButton").style.display="none",document.getElementById("answerContentInput").value="답변이 아직 없습니다."):(document.getElementById("createAnswerButton").style.display="none",document.getElementById("answerContentInput").value=n.content)}catch(t){console.error("Error fetching FAQ Answer:",t)}}()}})();